__author__ = 'Liam'class HTMLReport(object):    """    Creates a HTML based report based on results from the cleaner and analyser    objects.    """    def __init__(self, csv_file, reader, cleaner):        html_str = """<h1>CITS4401 Assignment 2 HTML Analysis Report on {}</h1><h2>Invalid Rows</h2>{}<h2>Empty Columns</h2>{}<h2></h2>        """.format(            csv_file,            self.list_creator(reader.invalid_data),            self.list_creator(cleaner.empty_columns)        )        html_file = open("{}_report.html".format(csv_file), "w")        html_file.write(html_str)        html_file.close()    @staticmethod    def list_creator(list_items):        html = '<ul>'        if list_items:            for i in list_items:                html += '<li>' + str(i) + '</li>'        else:            html += '<li>' + 'Empty' + '</li>'        html += '</ul>'        return htmlclass TxtReport(object):    """    Placeholder for creating a TXT based report.    """    def __init__(self):        pass